import React, { Component } from "react";
import Card from "./Card";

class GameFocus extends Component {
  state = {};

  renderPlatformButtons = () => {
    if (this.props.currentPage === "My Backlog") {
      return (
        <button
          key={this.props.game}
          value={this.props.game.name}
          className={`btn-search link preset focus-platform ${this.props.game.raw.platform.platform.slug}`}
        >
          {this.props.game.platform}
        </button>
      );
    } else {
      const platforms = this.props.game.platforms;

      if (
        platforms !== undefined &&
        platforms !== null &&
        platforms.length > 0
      ) {
        const gamePlatforms = platforms.map((platform, index) => {
          return (
            <button
              key={index}
              value={platform.platform.name}
              className={`btn-search link preset focus-platform ${platform.platform.slug}`}
            >
              {platform.platform.name}
            </button>
          );
        });

        return gamePlatforms;
      }
    }

    // return <h2>test</h2>;
  };

  addGame = event => {
    try {
      this.props.handleAddGame(this.props.game.slug);
    } catch (error) {
      console.log(error);
    } finally {
      this.props.handleClose();
    }
  };

  renderStatus = () => {
    const statusTypes = ["Owned", "Started", "Beaten", "Dropped"];

    if (this.props.currentPage === "Add Item") {
      const statusButtons = statusTypes.map((status, index) => {
        return (
          <button
            key={index}
            value={status}
            className={`btn-search link preset focus-status`}
          >
            {status}
          </button>
        );
      });
      return statusButtons;
    } else {
      const status = this.props.game.status;

      console.log("props.game", this.props.game);
      console.log("props.game.platform", this.props.game.platform);
      console.log("status", status);

      return (
        <button
          key={this.props.game.status}
          value={status}
          className={`btn-search link preset focus-status`}
        >
          {status}
        </button>
      );
    }
  };

  renderAddGamePortion = () => {
    if (this.props.currentPage == "Add Item") {
      return (
        <React.Fragment>
          <button
            className="btn btn-add-to-backlog link"
            onClick={this.addGame}
          >
            Add to backlog
          </button>
        </React.Fragment>
      );
    }
  };

  render() {
    return (
      <React.Fragment>
        <button className="btn link" onClick={this.props.handleClose}>
          Close focus
        </button>
        <br />
        <div className="focus-container">
          <div className="card-container-focus">
            <Card
              key={this.props.game.name}
              game={this.props.game}
              parentComponent={"Game Focus"}
              currentPage={this.props.currentPage}
            />
            <div className="focus-details">
              <div className="game-properties">
                <h2>Platforms:</h2>
                {this.renderPlatformButtons()}
                <h2>Status:</h2>
                {this.renderStatus()}
                <br />
                <br />
                {this.renderAddGamePortion()}
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default GameFocus;
