import React, { Component } from "react";
import Cards from "./Cards";
import AddItem from "./AddItem";

class MyBacklog extends Component {
  state = {
    addItemVisible: false
  };

  showAddItem = () => {
    this.setState({
      addItemVisible: true
    });
  };

  hideAddItem = () => {
    this.setState({
      addItemVisible: false
    });
  };

  renderContent = () => {
    if (this.state.addItemVisible) {
      return (
        <div className="add-item-container ">
          <AddItem
            handleClose={this.hideAddItem}
            handleSearch={this.props.handleSearch}
            handleAddGame={this.props.handleAddGame}
            handleGameFocus={this.props.handleGameFocus}
          />
        </div>
      );
    } else {
      return (
        <React.Fragment>
          <div className="card-container">
            <Cards
              games={this.props.games}
              parentComponent={"My Backlog"}
              handleGameFocus={this.props.handleGameFocus}
            />
          </div>
        </React.Fragment>
      );
    }
  };

  render() {
    return (
      <div>
        <div className="main">
          <div className="option-bar">
            <div className="option-bar-item link">
              <a className="link" onClick={this.showAddItem}>
                + Add Item
              </a>
            </div>
            <div className="option-bar-item-end link">
              <a className="link" href="">
                Sort By
              </a>
            </div>
          </div>
          <div className="content">{this.renderContent()}</div>
        </div>
      </div>
    );
  }
}

export default MyBacklog;
