import React, { Component, useLayoutEffect } from "react";
import Navbar from "./components/Navbar";
import Content from "./components/Content";
import Footer from "./components/Footer";
import Game from "./Game";

class App extends Component {
  state = {
    navItems: ["My Backlog", "Settings", "Account"],
    isLoaded: false,
    games: [],
    rawger: "",
    pageIndex: 0,
    navbarClasses: [],
    username: "idan91",
    myPlatforms: []
  };

  gameExists = (i_Game, i_GameList) => {
    let exists = false;

    i_GameList.forEach(game => {
      if (game.slug == i_Game) {
        exists = true;
      }
    });

    return exists;
  };

  addGameToList = async (i_Game, i_Platform = "playstation4", i_Status = 1) => {
    const myGameList = this.state.games;

    const gameExists = this.gameExists(i_Game, myGameList);

    if (gameExists) {
      console.log("Game already exits!");
    } else {
      let game = await this.getGameFromApi(i_Game)
        .then(data => {
          return data;
        })
        .then(resolvedGame => {
          const convertedGame = this.convertToGameObject(
            resolvedGame,
            i_Platform,
            i_Status
          );

          return convertedGame;
        })
        .catch(e => {
          console.log(e);
        });

      myGameList.push(game);

      this.setState({
        games: myGameList
      });
    }
  };

  componentDidMount() {
    // fetch(
    //   "https://api.rawg.io/api/games?dates=2019-01-01,2019-12-31&ordering=-added"
    // )
    //   .then(result => {
    //     return result.json();
    //   })
    //   .then(data => {
    //     this.setState({
    //       isLoaded: true,
    //       games: data.results
    //     });
    //   });

    this.startRawger();

    this.defineNavbarClasses(this.state.pageIndex);
  }

  startRawger = async () => {
    const Rawger = require("rawger");
    await Rawger({}).then(data => {
      this.setState({
        rawger: data
      });
    });
  };

  getGameFromApi = async i_GameSlug => {
    const Rawger = require("rawger");
    const { games } = await Rawger({});
    const results = await games
      .get(i_GameSlug)
      .then(result => {
        return result.get();
      })
      .then(data => {
        return data.raw;
      });

    return results;
  };

  getGameFromList = i_Game => {
    const gameList = this.state.games;

    console.log("i_Game", i_Game);
    console.log("gameList", gameList);

    const myGame = gameList.forEach(game => {
      console.log("game", game);
      console.log("game.slug", game.slug);
      if (i_Game === game.slug) {
        return game;
      }
    });

    return myGame;
  };

  searchGame = async i_GameName => {
    const Rawger = require("rawger");
    const { games } = await Rawger({});
    try {
      const results = await games
        .search(i_GameName)
        .then(result => {
          return result.raw();
        })
        .then(data => {
          return data;
        });

      const filteredResults = results.filter(item => item.rating > 0);

      return filteredResults;
    } catch (error) {
      return error.toString();
    }
  };

  convertToGameObject(i_Game, i_Platform, i_Status = 1) {
    return new Game(i_Game, i_Platform, i_Status);
  }

  switchPage = event => {
    const navItem = event.target.getAttribute("value");

    this.state.navItems.forEach((item, index) => {
      if (item === navItem) {
        this.setState({ pageIndex: index });
        this.defineNavbarClasses(index);
      }
    });
  };

  defineNavbarClasses = i_Index => {
    const navItems = this.state.navItems;
    let navClasses = [];

    const defaultClasses = "nav-link link";

    for (let i = 0; i < navItems.length; i++) {
      if (i === i_Index) {
        navClasses.push(`${defaultClasses} btn nav-selected`);
      } else {
        navClasses.push(`${defaultClasses}`);
      }
    }
    this.setState({
      navbarClasses: navClasses
    });
  };

  render() {
    return (
      <React.Fragment>
        <Navbar
          navItems={this.state.navItems}
          pageName={this.state.navItems[this.state.pageIndex]}
          pageIndex={this.state.pageIndex}
          handleClick={this.switchPage}
          classes={this.state.navbarClasses}
          username={this.state.username}
        />
        <div className="inner-body">
          <Content
            pageName={this.state.navItems[this.state.pageIndex]}
            pageIndex={this.state.pageIndex}
            games={this.state.games}
            handleSearch={this.searchGame}
            handleAddGame={this.addGameToList}
            handleGetGameFromApi={this.getGameFromApi}
            handleGetGameFromList={this.getGameFromList}
          />
        </div>
        <Footer />
      </React.Fragment>
    );
  }
}

export default App;
