import React, { Component } from "react";
import BCKLG_Logo from "../../resources/BCKLG_Logo.svg";
import BCKLG_Logo_vertical from "../../resources/BCKLG_Logo_vertical.svg";

class Logo extends Component {
  state = {
    logoSrc: BCKLG_Logo,
    logoClasses: "logo-img"
  };

  componentDidMount() {
    if (this.props.currentPage !== undefined) {
      this.defineLogo(this.props.currentPage);
    } else if (this.props.navbarType !== undefined) {
      this.defineLogo(this.props.navbarType);
    }
  }

  defineLogo = i_Page => {
    if (i_Page === "Login") {
      this.setState({
        logoSrc: BCKLG_Logo_vertical
      });
    } else {
      this.setState({
        logoSrc: BCKLG_Logo
      });
    }
    this.defineLogoClasses(i_Page);
  };

  defineLogoClasses = i_Context => {
    if (i_Context === "home") {
      this.setState({
        logoClasses: "logo-img logo-img-link link"
      });
    } else if (i_Context === "bcklg") {
      this.setState({
        logoClasses: "logo-img logo-img-link link"
      });
    } else if (i_Context === "Login") {
      this.setState({
        logoClasses: "logo-login logo-img"
      });
    } else if (i_Context === "Join") {
      this.setState({
        logoClasses: "logo-img logo-inline"
      });
    } else {
      this.setState({
        logoClasses: "logo-img"
      });
    }
  };

  renderLogo = i_Context => {
    if (i_Context === "home") {
      return (
        <a href={"/"}>
          <img
            className={this.state.logoClasses}
            src={this.state.logoSrc}
            alt=""
            width={this.props.width}
          />
        </a>
      );
    } else {
      return (
        <a href={"/my-backlog"}>
          <img
            className={this.state.logoClasses}
            src={this.state.logoSrc}
            alt=""
            width={this.props.width}
          />
        </a>
      );
    }
  };

  render() {
    return this.renderLogo(this.props.navbarType);
  }
}

export default Logo;
