import React, { Component } from "react";
import Cards from "./Cards";

class AddItem extends Component {
  state = {
    searchResults: []
  };

  componentDidMount() {
    let results = [];

    results.push(
      this.props.handleSearch().then(data => {
        return data;
      })
    );
    console.log(results[0]);

    this.setState({
      searchResults: results
    });
  }

  renderSearchResults = async => {
    let results = [];

    results.push(this.state.searchResults);

    return <Cards games={results} />;
  };

  // renderCities = async () => { // applies this.getCityForecast() to the array of city names
  //   if(this.state.cities) {
  //     const cityObj = await Promise.all(this.state.cities
  //       .map(city => this.getCityForecast(city)))
  //         .catch(e => {console.log(e)});
  //     console.log(cityObj); // Save response to this.cityObjects
  //   }
  // }

  renderResults = async () => {
    // applies this.getCityForecast() to the array of city names
    const cityObj = await Promise.all(
      this.state.cities.map(city => this.getCityForecast(city))
    ).catch(e => {
      console.log(e);
    });
    console.log(cityObj); // Save response to this.cityObjects
  };

  render() {
    return (
      <React.Fragment>
        <h1 className="sub-page-title">Add Item</h1>
        <h3 className="link" onClick={this.props.handleClose}>
          Close
        </h3>
        <div className="search-container">
          <div className="search">
            <input
              type="text"
              className="search-box"
              placeholder="Search Game"
            ></input>
          </div>
        </div>
        {this.renderSearchResults()}
      </React.Fragment>
    );
  }
}

export default AddItem;
