import React, { Component } from "react";
import NavItem from "./NavItem";

class Navbar extends Component {
  state = {
    aClassName: "nav-link",
    navItems: this.props.navItems,
    addedNavClasses: []
  };

  componentDidMount() {
    const navItems = this.state.navItems;
    const addedClasses = navItems.map(item => " ");
    this.setState({ addedNavClasses: addedClasses });
  }

  // TODO - FIX!!
  handleClick = event => {
    console.log(event.target);
  };

  drawNabBar = () => {
    const navItemArr = this.state.navItems;
    let classes = [];

    for (let i = 0; i < navItemArr.length; i++) {
      classes.push(`${this.state.aClassName} ${this.state.addedNavClasses[i]}`);
    }
    const navbar = navItemArr.map((item, index) => {
      return (
        /* <li className="nav-item" key={index}>
          <a
            className={classes}
            value={item}
            href=""
            key={index}
            onClick={this.handleClick}
          >
            {item}
          </a>
        </li>
      */

        <NavItem
          classes={classes}
          value={item}
          href=""
          key={item}
          value={item}
          handleClick={this.handleClick}
        />
      );
    });

    return navbar;
  };

  render() {
    return (
      <div className="navbar">
        <nav className="nav">
          <div className="logo">BCKLG</div>
          <ul className="nav-list">{this.drawNabBar()}</ul>
        </nav>
      </div>
    );
  }
}

export default Navbar;
