import React, { Component } from "react";
import Card from "./Card";

class Cards extends Component {
  state = {
    gameFocus: false
  };

  handleCardClick = event => {
    let target = event.target;

    while (!target.classList.value.includes("game-card")) {
      target = target.parentNode;
    }

    const game = target.getAttribute("slug");

    this.activateGameFocus();
    this.renderComponents(game, this.props.parentComponent);

    if (this.props.parentComponent === "Add Item") {
      this.props.handleAddGame(game);
    }
  };

  drawCards = () => {
    const cardsArr = this.props.games;

    let cardDisplay;

    cardDisplay = cardsArr.map((item, index) => {
      return (
        <Card key={index} game={item} handleClick={this.handleCardClick} />
      );
    });

    return cardDisplay;
  };

  activateGameFocus() {
    this.setState({
      gameFocus: true
    });
  }

  renderComponents(i_Game = null, i_ParentComponent = null) {
    if (
      !this.state.gameFocus ||
      (i_Game === null && i_ParentComponent == null)
    ) {
      return this.drawCards();
    } else {
      return this.props.handleGameFocus(i_Game, i_ParentComponent);
    }
    // if (this.state.gameFocus) {
    //   this.props.handleGameFocus(game, this.props.parentComponent);
    // } else {
    //   return this.drawCards();
    // }
  }

  render() {
    return (
      <React.Fragment>
        <br />
        {this.renderComponents()}
      </React.Fragment>
    );
  }
}

export default Cards;
