import React, { Component } from "react";
import Cards from "./Cards";

class AddItem extends Component {
  state = {
    searchResults: []
  };

  componentDidMount() {
    let results = [];

    const result = async () => await this.props.handleSearch();

    this.setState({
      searchResults: results
    });
  }

  renderSearchResults() {
    const results = this.props.handleSearch();

    if (results > 0) {
      return <Cards games={results} />;
    }
  }

  render() {
    console.log(this.state.searchResults);
    return (
      <React.Fragment>
        <h1 className="sub-page-title">Add Item</h1>
        <h3 className="link" onClick={this.props.handleClose}>
          Close
        </h3>
        <div className="search-container">
          <div className="search">
            <input
              type="text"
              className="search-box"
              placeholder="Search Game"
            ></input>
          </div>
        </div>
        {this.renderSearchResults()}
      </React.Fragment>
    );
  }
}

export default AddItem;
