import React, { Component } from "react";
import Cards from "./Cards";

class AddItem extends Component {
  state = {
    searchResults: [],
    query: "",
    year: 2020,
    errorMessage: "Could not find game! Please try again",
    displayErrorMessage: false,
    searchInitiated: false,
    gameFocusVisible: false
  };

  toggleGameFocus = () => {
    if (this.state.gameFocusVisible) {
      this.setState({
        gameFocusVisible: false
      });
    } else {
      this.setState({
        gameFocusVisible: true
      });
    }
  };

  handleInputChange = event => {
    let newQuery = event.target.value;
    this.setState({
      query: newQuery
    });
  };

  handleSubmitSearch = event => {
    // console.log(event.target);
    this.renderResults(this.state.query);
  };

  componentDidMount() {
    // this.renderResults();
  }

  renderResults = async i_GameName => {
    this.setState({
      displayErrorMessage: false
    });

    let allResults = [];

    let results = await this.props
      .handleSearch(i_GameName)
      .then(data => {
        return data;
      })
      .catch(e => {
        console.log(e);
      });

    if (typeof results === typeof "string") {
      this.setState({ displayErrorMessage: true });
      return;
    }

    allResults = results;

    this.setState({
      searchResults: allResults,
      searchInitiated: true
    });
  };

  handleCardClick = () => {
    this.setState({
      gameFocusVisible: true
    });
  };

  renderItems() {
    if (
      this.state.displayErrorMessage ||
      (this.state.searchResults.length < 1 && this.state.searchInitiated)
    ) {
      return <h2>{this.state.errorMessage}</h2>;
    } else if (this.state.gameFocusVisible) {
      return (
        <div className="game-focus-container">
          <GameFocus
            game={
              this.getFocusGame()
              /*this.props
                .games[0]*/
            }
            parentComponent={"My Backlog"}
          />
        </div>
      );
    } else {
      return (
        <div className="card-container">
          <Cards
            games={this.state.searchResults}
            handleCardClick={this.handleCardClick}
            handleAddGame={this.props.handleAddGame}
            parentComponent={"Add Item"}
          />
        </div>
      );
    }
  }

  populateYears = () => {
    const currentYear = new Date().getFullYear();

    let yearArray = [];

    console.log(currentYear);

    for (let i = currentYear; i > 1969; i--) {
      yearArray.push(
        <option className="year" key={i} value={i}>
          {i}
        </option>
      );
    }

    return yearArray;
  };

  handleKeyDown = event => {
    if (event.keyCode === 13) {
      this.renderResults(this.state.query);
    }
  };

  handleChange = event => {
    const { value } = event.target;
    this.setState({
      year: value
    });
  };

  handleSearchPreset = async event => {
    const { name } = event.target;
    console.log(name);
    const year = this.state.year;
    let linkSuffix;

    if (name === "Most Popular") {
      linkSuffix = `added`;
    } else {
      linkSuffix = `rating`;
    }

    fetch(
      `https://api.rawg.io/api/games?dates=${year}-01-01,${year}-12-31&ordering=-${linkSuffix}`
    )
      .then(result => {
        return result.json();
      })
      .then(data => {
        console.log(data);
        this.setState({
          searchResults: data.results
        });
      });
  };

  render() {
    return (
      <React.Fragment>
        <br />
        <div className="sub-page-header">
          {/* <h1 className="sub-page-title">Add Item</h1> */}
          <button
            className="btn link close-add-item"
            onClick={this.props.handleClose}
          >
            Close
          </button>
        </div>
        <div className="search-container">
          <div className="search">
            <input
              type="text"
              className="search-box"
              placeholder="Which game are you looking for?"
              value={this.state.query}
              onChange={this.handleInputChange}
              onKeyDown={this.handleKeyDown}
            ></input>
            <button
              className="btn-search link"
              onClick={this.handleSubmitSearch}
            >
              Search
            </button>
          </div>
          <div className="search-presets">
            <button
              className="btn-search link preset"
              onClick={this.handleSearchPreset}
              name="Most Popular"
            >
              Most Popular
            </button>
            <button
              className="btn-search link preset"
              onClick={this.handleSearchPreset}
              name="Highest Rated"
            >
              Highest Rated
            </button>
            <select
              className="btn-search link preset year-select"
              onChange={this.handleChange}
            >
              {this.populateYears()}
            </select>
          </div>
        </div>
        {this.renderItems()}
      </React.Fragment>
    );
  }
}

export default AddItem;
