import {
  SET_USER,
  SET_ERRORS,
  CLEAR_ERRORS,
  LOADING_UI,
  LOADING_USER,
  SET_UNAUTHENTICATED
} from "../types";
import axios from "axios";

const config = {
  apiKey: "AIzaSyBk066lHfZjW5w5vgzUAjXfIeVhvyqZiLE",
  authDomain: "bcklg-6b059.firebaseapp.com",
  databaseURL: "https://bcklg-6b059.firebaseio.com",
  projectId: "bcklg-6b059",
  storageBucket: "bcklg-6b059.appspot.com",
  messagingSenderId: "337945780002",
  appId: "1:337945780002:web:d4d3fbc3659c7094a58f7b",
  measurementId: "G-13G073NX99"
};
const firebase = require("firebase");
firebase.initializeApp(config);

export const loginUser = (userData, history) => dispatch => {
  dispatch({ type: LOADING_UI });
  axios
    .post("/login", userData)
    .then(result => {
      setAuthrizationHeader(result.data.token);
      dispatch(getUserData());
      dispatch({ type: CLEAR_ERRORS });
      history.push("/my-backlog");
    })
    .catch(err => {
      dispatch({
        type: SET_ERRORS,
        payload: err.response.data
      });
    });
};

