import React, { Component } from "react";
import MyBacklog from "./MyBacklog.js";
import Settings from "./Settings.js";
import Account from "./Account.js";

class Content extends Component {
  state = {
    content: [
      <MyBacklog
        pageName="My Backlog"
        handleSearch={this.props.handleSearch}
      />,
      <Settings pageName="Settings" />,
      <Account pageName="Account" />
    ]
  };

  renderOptionBar() {
    return (
      <React.Fragment>
        <div className="option-bar-item link">
          <a className="link" onClick={this.showAddItem}>
            + Add Item
          </a>
        </div>
        <div className="option-bar-item-end link">
          <a className="link" href="">
            Sort By
          </a>
        </div>
      </React.Fragment>
    );
  }

  render() {
    return (
      <React.Fragment>
        <h1 className="page-title">{this.props.pageName}</h1>
        <div className="option-bar">
          <div className="option-bar-item link">
            <a className="link" onClick={this.showAddItem}>
              + Add Item
            </a>
          </div>
          <div className="option-bar-item-end link">
            <a className="link" href="">
              Sort By
            </a>
          </div>
        </div>
        {React.cloneElement(this.state.content[this.props.pageIndex], {
          games: this.props.games
        })}
      </React.Fragment>
    );
  }
}

export default Content;
