import React, { Component } from "react";

class Cards extends Component {
  state = {
    platformClasses: "platform"
  };

  componentDidMount() {
    this.assignPlatformClasses();
  }

  assignPlatformClasses = () => {
    if (this.props.currentPage === "My Backlog") {
      this.setState({
        platformClasses: `platform ${this.props.game.platform.slug}`
      });
    } else {
      this.setState({
        platformClasses: `platform ${this.props.game.platform}`
      });
    }
  };

  renderGameDetails = () => {
    let status = "";
    let platform = "";

    if (this.props.currentPage === "My Backlog") {
      status = (
        <p className="game-detail game-status">
          <a className="game-detail label">Status:</a>
          <a className="game-detail">{this.props.game.status}</a>
        </p>
      );

      platform = (
        <div className={this.state.platformClasses}>
          <p className="platform-name">{this.props.game.platform.name}</p>
        </div>
      );
    }

    return (
      <React.Fragment>
        <div className="game-detail-container">
          <p className="game-detail game-rating">
            <a className="game-detail label">Rating:</a>
            <a className="game-detail">{this.props.game.rating}</a>
          </p>
          <p className="game-detail game-release">
            <a className="game-detail label">Released:</a>
            <a className="game-detail">{this.props.game.released}</a>
          </p>
          {status}
        </div>
        {platform}
      </React.Fragment>
    );
  };

  render() {
    return (
      <div
        key={this.props.game.name}
        className="game-card link"
        onClick={this.props.handleClick}
        game={this.props.game}
        name={this.props.game.name}
        slug={this.props.game.slug}
      >
        <div className="img-container">
          <img
            className="game-img"
            src={this.props.game.background_image}
            alt={this.props.game.name}
          />
        </div>
        <div className="title-container">
          <a className="game-title">{this.props.game.name}</a>
        </div>
        {this.renderGameDetails()}
      </div>
    );
  }
}

export default Cards;
