import React, { Component, useLayoutEffect } from "react";
import Navbar from "./components/Navbar";
import Content from "./components/Content";
import Footer from "./components/Footer";

class App extends Component {
  state = {
    navItems: ["My Backlog", "Settings", "Account"],
    isLoaded: false,
    games: []
  };

  componentDidMount() {
    fetch(
      "https://api.rawg.io/api/games?dates=2019-01-01,2019-12-31&ordering=-added"
    )
      .then(result => {
        return result.json();
      })
      .then(data => {
        this.setState({
          isLoaded: true,
          games: data.results
        });
      });
  }

  // getRAWger = async function() {
  //   const Rawger = require("rawger");
  //   const rawger = await Rawger();

  //   return rawger;
  // };

  // componentDidMount() {
  //   const rawger = this.getRAWger();
  //   const { games } = rawger;

  //   this.setState({
  //     games: games
  //   });
  // }

  render() {
    return (
      <React.Fragment>
        <Navbar navItems={this.state.navItems} />
        <div className="inner-body">
          <Content
            pageName={this.state.navItems[0]}
            myGames={this.state.myGames}
          />
        </div>
        <Footer />
      </React.Fragment>
    );
  }
}

export default App;
