import React, { Component } from "react";

class Navbar extends Component {
  state = {
    aClassName: "nav-link",
    navItems: this.props.navItems,
    addedNavClasses: []
  };

  componentDidMount() {
    const navItems = this.state.navItems;
    const addedClasses = navItems.map(item => "nav-selected");
    this.setState({ addedNavClasses: addedClasses });
  }

  // TODO - FIX!!
  handleClick = event => {
    console.log(event.target);

    /*const menuItem = event.target.value;
    const navItemArr = this.state.navItems;

    let addedClasses = navItemArr.array.forEach((item, index) => {
      if (menuItem === navItemArr) {
        addedClasses[index] = "nav-selected";
      } else {
        addedClasses[index] = "";
      }
    });

    console.log(addedClasses);

    this.setState({
      addedNavClasses: addedClasses
    });*/
  };

  drawNabBar = () => {
    const navItemArr = this.state.navItems;

    const navbar = navItemArr.map((item, index) => {
      const classes =
        this.state.aClassName + " " + this.state.addedNavClasses[index];

      return (
        <li className="nav-item" key={index}>
          <a
            className={classes}
            value={item}
            href=""
            key={index}
            onClick={this.handleClick}
          >
            {item}
          </a>
        </li>
      );
    });

    return navbar;
  };

  render() {
    return (
      <div className="navbar">
        <nav className="nav">
          <div className="logo">BCKLG</div>
          <ul className="nav-list">{this.drawNabBar()}</ul>
        </nav>
      </div>
    );
  }
}

export default Navbar;
