import React, { Component } from "react";
import Card from "./Card";
import GameFocus from "./GameFocus";

class Cards extends Component {
  state = {
    gameFocusVisible: false,
    gameForFocus: ""
  };

  toggleGameFocus = i_Game => {
    this.setState({
      gameFocusVisible: true,
      gameForFocus: i_Game
    });
  };

  handleCardClick = event => {
    let target = event.target;

    while (!target.classList.value.includes("game-card")) {
      target = target.parentNode;
    }

    const game = target.getAttribute("slug");

    this.toggleGameFocus(game);

    // if (this.props.parentComponent === "Add Item") {
    //   this.props.handleAddGame(game);
    // }
  };

  renderItems() {
    if (this.state.gameFocusVisible) {
      this.renderGameFocus();
    } else {
      this.drawCards();
    }
  }

  renderGameFocus() {
    const game = this.state.gameForFocus;

    return <GameFocus game={game} currentPage={this.parentComponent} />;
  }

  drawCards = () => {
    const cardsArr = this.props.games;

    let cardDisplay;

    cardDisplay = cardsArr.map((item, index) => {
      return (
        <Card
          key={index}
          game={item}
          handleClick={this.handleCardClick}
          parentComponent={"Cards"}
          parentOfParentComponent={this.props.parentComponent}
        />
      );
    });

    return cardDisplay;
  };

  activateGameFocus() {
    this.setState({
      gameFocus: true
    });
  }

  render() {
    return (
      <React.Fragment>
        <br />
        {this.renderItems()}
      </React.Fragment>
    );
  }
}

export default Cards;
