import React, { Component } from "react";
import Card from "./Card";
import GameFocus from "./GameFocus";

class Cards extends Component {
  state = {
    gameFocusVisible: false,
    gameForFocus: "",
    gameFocus: ""
  };

  handleCardClick = event => {
    let target = event.target;

    while (!target.classList.value.includes("game-card")) {
      target = target.parentNode;
    }

    const game = target;

    const gameSlug = game.getAttribute("slug");

    // this.showGameFocus(gameSlug);
    this.renderGameFocus(gameSlug);

    // if (this.props.parentComponent === "Add Item") {
    //   this.props.handleAddGame(gameSlug);
    // }
  };

  // showGameFocus = i_Game => {
  //   this.setState({
  //     gameFocusVisible: true,
  //     gameForFocus: i_Game
  //   });
  // };

  drawCards = () => {
    const cardsArr = this.props.games;

    let cardDisplay;

    cardDisplay = cardsArr.map((item, index) => {
      return (
        <Card key={index} game={item} handleClick={this.handleCardClick} />
      );
    });

    return cardDisplay;
  };

  renderGameFocus = async i_Game => {
    try {
      await this.props.handleGetGame(i_Game).then(game => {
        this.setState({
          gameFocusVisible: true,
          gameForFocus: game,
          gameFocus: (
            <GameFocus game={game} currentPage={this.props.parentComponent} />
          )
        });
      });
    } catch (error) {
      console.log(err);
    }
  };

  renderItems() {
    if (this.state.gameFocusVisible) {
      return this.state.gameFocus;
    } else {
      return this.drawCards();
    }
  }

  render() {
    return (
      <React.Fragment>
        <br />
        {this.renderItems()}
      </React.Fragment>
    );
  }
}

export default Cards;
