import React, { Component } from "react";
import Card from "./Card";

class Cards extends Component {
  state = {
    gameFocusVisible: false
  };

  toggleGameFocus = () => {
    this.setState({
      gameFocusVisible: true
    });
  };

  handleCardClick = event => {
    let target = event.target;

    while (!target.classList.value.includes("game-card")) {
      target = target.parentNode;
    }

    const game = target.getAttribute("slug");

    this.toggleGameFocus();

    // if (this.props.parentComponent === "Add Item") {
    //   this.props.handleAddGame(game);
    // }
  };

  drawCards = () => {
    const cardsArr = this.props.games;

    let cardDisplay;

    cardDisplay = cardsArr.map((item, index) => {
      return (
        <Card
          key={index}
          game={item}
          handleClick={this.handleCardClick}
          parentComponent={"Cards"}
          parentOfParentComponent={this.props.parentComponent}
        />
      );
    });

    return cardDisplay;
  };

  activateGameFocus() {
    this.setState({
      gameFocus: true
    });
  }

  render() {
    return (
      <React.Fragment>
        <br />
        {this.drawCards()}
      </React.Fragment>
    );
  }
}

export default Cards;
