import React, { Component } from "react";
import Card from "./Card";

class GameFocus extends Component {
  state = {
    selectedPlatfrom: "",
    platformButtonClasses: "btn-search link preset focus-button focus-platform",
    selectedStatus: "",
    statusButtonClasses: "btn-search link preset focus-button focus-status",
    selectedButtonClass: "focus-button-selected"
  };

  handleOptionChange = event => {
    const { target } = event;

    if (target.getAttribute("selected") === "false") {
      tar;
    }
  };

  renderPlatformButtons = () => {
    let platformHeader;
    let gamePlatforms;

    if (this.props.currentPage === "My Backlog") {
      platformHeader = <h2>Platform:</h2>;

      gamePlatforms = (
        <button
          key={this.props.game.platform.slug}
          value={this.props.game.platform.slug}
          className={`${this.state.platformButtonClasses} ${this.props.game.platform.slug}`}
        >
          {this.props.game.platform.name}
        </button>
      );
    } else {
      platformHeader = <h2>Platforms:</h2>;

      const platforms = this.props.game.platforms;

      if (
        platforms !== undefined &&
        platforms !== null &&
        platforms.length > 0
      ) {
        gamePlatforms = platforms.map((platform, index) => {
          return (
            <button
              key={platform.platform.slug}
              value={platform.platform.slug}
              selected={this.state.selectedPlatform === platform.platform.slug}
              className={`${this.state.platformButtonClasses} ${this.state
                .selectedPlatform === platform.platform.slug &&
                platform.platform.slug}`}
            >
              {platform.platform.name}
            </button>
          );
        });
      }
    }

    return (
      <React.Fragment>
        {platformHeader}
        {gamePlatforms}
      </React.Fragment>
    );
    // return <h2>test</h2>;
  };

  addGame = event => {
    try {
      this.props.handleAddGame(this.props.game.slug);
    } catch (error) {
      console.log(error);
    } finally {
      this.props.handleClose();
    }
  };

  renderStatus = () => {
    const statusTypes = ["Owned", "Started", "Beaten", "Dropped"];
    const statusHeader = <h2>Status:</h2>;
    let statusToRender;

    if (this.props.currentPage === "Add Item") {
      statusToRender = statusTypes.map((status, index) => {
        return (
          <button
            key={index}
            value={status}
            selected="false"
            className={`${this.state.statusButtonClasses} ${this.state.selectedButtonClass}`}
          >
            {status}
          </button>
        );
      });
    } else {
      const status = this.props.game.status;
      console.log("props.game", this.props.game);
      console.log("props.game.platform", this.props.game.platform);
      console.log("status", status);

      statusToRender = (
        <React.Fragment>
          <button
            key={this.props.game.status}
            value={status}
            selected="false"
            className={`${this.state.statusButtonClasses} ${this.state.selectedButtonClass}`}
          >
            {status}
          </button>
        </React.Fragment>
      );
    }

    return (
      <React.Fragment>
        {statusHeader}
        {statusToRender}
      </React.Fragment>
    );
  };

  renderAddGamePortion = () => {
    if (this.props.currentPage == "Add Item") {
      return (
        <React.Fragment>
          <button
            className="btn btn-add-to-backlog link"
            onClick={this.addGame}
          >
            Add to backlog
          </button>
        </React.Fragment>
      );
    }
  };

  render() {
    return (
      <React.Fragment>
        <br />
        <button className="btn link" onClick={this.props.handleClose}>
          Close
        </button>
        {(() => {
          if (this.props.currentPage === "My Backlog") {
            return <button className="btn link">Edit</button>;
          }
        })()}
        <br />
        <br />
        <div className="focus-container">
          <div className="card-container-focus">
            <Card
              key={this.props.game.name}
              game={this.props.game}
              parentComponent={"Game Focus"}
              currentPage={this.props.currentPage}
            />
            <div className="focus-details">
              <div className="game-properties">
                {this.renderPlatformButtons()}
                {this.renderStatus()}
                <br />
                <br />
                {this.renderAddGamePortion()}
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default GameFocus;
