import React, { Component } from "react";
import Card from "./Card";

class GameFocus extends Component {
  state = {};

  renderPlatformButtons = () => {
    const platforms = this.props.game.platforms;

    if (platforms !== undefined && platforms !== null && platforms.length > 0) {
      const gamePlatforms = platforms.map(platform => {
        return <button className="platform">{platform}</button>;
      });

      return gamePlatforms;
    }
  };

  render() {
    return (
      <React.Fragment>
        <br />
        <h1>Focus</h1>
        <div className="card-container-focus">
          <Card
            key={this.props.game.name}
            game={this.props.game}
            parentComponent={"Game Focus"}
            parentOfParentComponent={this.props.parentComponent}
          />
          <div className="game-properties">
            <h2>Platforms:</h2>
            <p>{this.renderPlatformButtons()}</p>
            <h2>Status:</h2>
            <button className="btn btn-add-to-backlog link">
              Add to backlog
            </button>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default GameFocus;
