import React, { Component } from "react";
import NavItem from "./NavItem";

class Navbar extends Component {
  state = {
    aClassName: "nav-link",
    navItems: this.props.navItems,
    classes: []
  };

  componentDidMount() {
    const navItems = this.state.navItems;
    const navClasses = navItems.map(item => `${this.state.aClassName}`);

    this.setState({ classes: navClasses });
  }

  // TODO - FIX!!
  handleClick = event => {
    let menuItem = event.target;

    const navItemArr = this.state.navItems;

    /*navItemArr.array.forEach(item => {});
     */

    menuItem.classList = `${this.state.aClassName} nav-selected`;
    console.log(menuItem);
  };

  drawNabBar = () => {
    const navItemArr = this.state.navItems;
    // let classes = [];

    // for (let i = 0; i < navItemArr.length; i++) {
    //   classes.push(this.state.classes);
    // }
    const navbar = navItemArr.map((item, index) => {
      return (
        <NavItem
          classes={this.state.classes[index]}
          value={item}
          href=""
          value={item}
          handleClick={this.handleClick}
        />
      );
    });

    return navbar;
  };

  render() {
    return (
      <div className="navbar">
        <nav className="nav">
          <div className="logo">BCKLG</div>
          <ul className="nav-list">{this.drawNabBar()}</ul>
        </nav>
      </div>
    );
  }
}

export default Navbar;
