import React, { Component } from "react";
import Cards from "./Cards";

class AddItem extends Component {
  state = {
    searchResults: [],
    query: ""
  };

  handleInputChange = event => {
    let newQuery = event.target.value;
    this.setState({
      query: newQuery
    });
  };

  handleSubmitSearch = event => {
    // console.log(event.target);
    this.renderResults(this.state.query);
  };

  componentDidMount() {
    // this.renderResults();
  }

  renderResults = async () => {
    let allResults = [];

    let results = await this.props
      .handleSearch()
      .then(data => {
        return data;
      })
      .catch(e => {
        console.log(e);
      });

    allResults = results;

    this.setState({
      searchResults: allResults
    });
  };

  renderItems() {
    return <Cards games={this.state.searchResults} />;
    // return <h1>test</h1>;
  }

  render() {
    return (
      <React.Fragment>
        {/* <h1 className="sub-page-title">Add Item</h1> */}
        <h3 className="link" onClick={this.props.handleClose}>
          Close
        </h3>
        <div className="search-container">
          <div className="search">
            <input
              type="text"
              className="search-box"
              placeholder="Search Game"
              value={this.state.query}
              onChange={this.handleInputChange}
            ></input>
            <button onClick={this.handleSubmitSearch}>Search</button>
          </div>
        </div>
        {this.renderItems()}
      </React.Fragment>
    );
  }
}

export default AddItem;
