import React, { Component } from "react";
import Card from "./Card";

class GameFocus extends Component {
  state = {
    selectedPlatform: "",
    platformButtonClasses: "btn-search link preset focus-button focus-platform",
    selectedStatus: "",
    statusButtonClasses: "btn-search link preset focus-button focus-status",
    selectedButtonClass: "focus-button-selected",
    editGame: false
  };

  componentDidMount() {
    if (this.props.currentPage === "Add Item") {
      this.props.handleSearchVisibility();
    }
  }

  handleOptionChange = event => {
    const { target } = event;
    const type = target.getAttribute("type");

    if (type === "Platform") {
      this.setState({
        selectedPlatform: target.value
      });
    } else {
      this.setState({
        selectedStatus: target.value
      });
    }
  };

  toggleEditGame = () => {
    this.setState({
      editGame: true
    });
  };

  renderPlatformButtons = () => {
    let platformHeader;
    let gamePlatforms;

    if (this.props.currentPage === "My Backlog" && !this.state.editGame) {
      platformHeader = <h2>Platform:</h2>;

      gamePlatforms = (
        <button
          key={this.props.game.platform.slug}
          value={this.props.game.platform.name}
          className={`${this.state.platformButtonClasses} ${this.props.game.platform.slug}`}
        >
          {this.props.game.platform.name}
        </button>
      );
    } else {
      platformHeader = <h2>Platforms:</h2>;
      let game;

      if (this.props.currentPage === "My Backlog") {
        game = this.props.game.raw;
      } else {
        game = this.props.game;
      }

      const platforms = game.platforms;

      if (
        platforms !== undefined &&
        platforms !== null &&
        platforms.length > 0
      ) {
        gamePlatforms = platforms.map((platform, index) => {
          const isSelected =
            this.state.selectedPlatform === platform.platform.name;

          return (
            <button
              key={platform.platform.slug}
              value={platform.platform.name}
              type="Platform"
              selected={isSelected}
              className={`${this.state.platformButtonClasses} ${
                platform.platform.slug
              } ${isSelected && this.state.selectedButtonClass}`}
              onClick={this.handleOptionChange}
            >
              {platform.platform.name}
            </button>
          );
        });
      }
    }

    return (
      <React.Fragment>
        {platformHeader}
        {gamePlatforms}
      </React.Fragment>
    );
    // return <h2>test</h2>;
  };

  addGame = () => {
    try {
      if (this.validateSelections()) {
        this.props.handleAddGame(
          this.props.game.slug,
          this.state.selectedPlatform,
          this.state.selectedStatus
        );
        this.props.handleClose();
      } else {
        alert("Please select a platform and a status!");
      }
    } catch (error) {
      console.log(error);
    }
  };

  editGame = () => {
    try {
      if (this.validateSelections()) {
        this.props.handleEditGame(
          this.props.game,
          this.state.selectedPlatform,
          this.state.selectedStatus
        );
        this.props.handleClose();
      } else {
        alert("Please select a platform and a status!");
      }
    } catch (error) {
      console.log(error);
    }
  };

  validateSelections() {
    return (
      this.state.selectedPlatform !== "" && this.state.selectedStatus !== ""
    );
  }

  renderStatus = () => {
    const statusTypes = ["Owned", "Started", "Beaten", "Dropped"];
    const statusHeader = <h2>Status:</h2>;
    let statusToRender;

    if (this.props.currentPage === "Add Item" || this.state.editGame) {
      statusToRender = statusTypes.map((status, index) => {
        const isSelected = this.state.selectedStatus === status;

        return (
          <button
            key={index}
            value={status}
            selected={isSelected}
            className={`${this.state.statusButtonClasses} ${isSelected &&
              this.state.selectedButtonClass}`}
            onClick={this.handleOptionChange}
          >
            {status}
          </button>
        );
      });
    } else {
      const status = this.props.game.status;

      statusToRender = (
        <React.Fragment>
          <button
            key={this.props.game.status}
            value={status}
            selected={false}
            className={`${this.state.statusButtonClasses}`}
          >
            {status}
          </button>
        </React.Fragment>
      );
    }

    return (
      <React.Fragment>
        {statusHeader}
        {statusToRender}
      </React.Fragment>
    );
  };

  renderBottomPortion = () => {
    if (this.props.currentPage == "Add Item") {
      return (
        <React.Fragment>
          <button
            className="btn btn-add-to-backlog link"
            onClick={this.addGame}
          >
            Add To Backlog
          </button>
        </React.Fragment>
      );
    } else if (this.state.editGame) {
      return (
        <React.Fragment>
          <button className="btn btn-save-edits link" onClick={this.editGame}>
            Save Edits
          </button>
          <button className="btn btn-close link" onClick={this.deleteGame}>
            Delete From Backlog
          </button>
        </React.Fragment>
      );
    }
  };

  render() {
    return (
      <React.Fragment>
        <br />
        <button className="btn link btn-close" onClick={this.props.handleClose}>
          Close
        </button>
        {(() => {
          if (this.props.currentPage === "My Backlog") {
            return (
              <button className="btn link" onClick={this.toggleEditGame}>
                Edit
              </button>
            );
          }
        })()}
        <br />
        <br />
        <div className="focus-container">
          <div className="card-container-focus">
            <Card
              key={this.props.game.name}
              game={this.props.game}
              parentComponent={"Game Focus"}
              currentPage={this.props.currentPage}
            />
            <div className="focus-details">
              <div className="game-properties">
                {this.renderPlatformButtons()}
                {this.renderStatus()}
                <br />
                <br />
                {this.renderBottomPortion()}
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default GameFocus;
