import React, { Component } from "react";

class Cards extends Component {
  state = {};
  render() {
    return (
      <div key={this.props.item.name} className="game-card">
        <div className="img-container">
          <img
            className="game-img"
            src={this.props.item.background_image}
            alt={this.props.item.name}
          />
        </div>
        <div className="title-container">
          <p className="game-title">{this.props.item.name}</p>
        </div>
        <p className="game-detail game-rating">
          Rating: {this.props.item.rating}
        </p>
        <p className="game-detail game-release">
          Released: {this.props.item.released}
        </p>
        <text className="game-detail game-release">Status:</text>
        <text className="game-etail game-status">Owned</text>
        <div className="platform ps4">
          <p className="platform-name">PS4</p>
        </div>
      </div>
    );
  }
}

export default Cards;
