import React, { Component } from "react";
import MyBacklog from "./MyBacklog.js";
import Settings from "./Settings.js";
import Account from "./Account.js";
import GameFocus from "./GameFocus";

class Content extends Component {
  state = {
    content: [
      <MyBacklog
        pageName="My Backlog"
        handleSearch={this.props.handleSearch}
        handleAddGame={this.props.handleAddGame}
        handleGameFocus={this.displayGameFocus}
      />,
      <Settings pageName="Settings" />,
      <Account pageName="Account" />
    ]
  };

  displayGameFocus = async (i_Game, i_ParentComponent) => {
    // TODO - code duplicate with App.addGameToList
    let game = await this.getGame(i_Game)
      .then(data => {
        return data;
      })
      .then(resolvedGame => {
        return (
          <GameFocus game={resolvedGame} parentComponent={i_ParentComponent} />
        );
      });
    // return <GameFocus game={i_Game} parentComponent={i_ParentComponent} />;
  };

  render() {
    return (
      <React.Fragment>
        <h1 className="page-title">{this.props.pageName}</h1>
        {React.cloneElement(this.state.content[this.props.pageIndex], {
          games: this.props.games
        })}
      </React.Fragment>
    );
  }
}

export default Content;
