import React, { Component, useLayoutEffect } from "react";
import Navbar from "./components/Navbar";
import Content from "./components/Content";
import Footer from "./components/Footer";

class App extends Component {
  state = {
    navItems: ["My Backlog", "Settings", "Account"],
    isLoaded: false,
    games: [],
    rawger: "",
    pageIndex: 0
  };

  componentDidMount() {
    fetch(
      "https://api.rawg.io/api/games?dates=2019-01-01,2019-12-31&ordering=-added"
    )
      .then(result => {
        return result.json();
      })
      .then(data => {
        this.setState({
          isLoaded: true,
          games: data.results
        });
      });
  }

  switchPage = event => {
    const navItem = event.target;

    console.log(navItem);

    this.state.navItems.forEach(item => {
      if (item == navItem.value) {
        const index = this.state.navItems.indexOf(item);
        this.setState({ pageIndex: index });
      }
    });
  };

  render() {
    return (
      <React.Fragment>
        <Navbar
          navItems={this.state.navItems}
          pageName={this.state.navItems[this.state.pageIndex]}
          handleClick={this.switchPage}
        />
        <div className="inner-body">
          <Content
            pageName={this.state.navItems[this.state.pageIndex]}
            pageIndex={this.state.pageIndex}
            games={this.state.games}
          />
        </div>
        <Footer />
      </React.Fragment>
    );
  }
}

export default App;
