import React, { Component } from "react";
import Cards from "./Cards";
import AddItem from "./AddItem";

class MyBacklog extends Component {
  state = {
    addItemVisible: false
  };

  showAddItem = () => {
    this.setState({
      addItemVisible: true
    });
  };

  hideAddItem = () => {
    this.setState({
      addItemVisible: false
    });
  };

  renderContent = () => {
    if (this.state.addItemVisible) {
      return (
        <div className="add-item-container">
          <AddItem handleClose={this.hideAddItem} />
        </div>
      );
    } else {
      return (
        <div className="card-container">
          <Cards games={this.props.games} />
        </div>
      );
    }
  };

  render() {
    return (
      <div className="fade-in">
        <h1 className="page-title">{this.props.pageName}</h1>
        <div className="option-bar">
          <div className="add-item">
            <a onClick={this.showAddItem}>+ Add Item</a>
          </div>
          <div className="sort-by">
            <a href="">Sort By</a>
          </div>
        </div>
        <div className="content">{this.renderContent()}</div>
      </div>
    );
  }
}

export default MyBacklog;
