import React, { Component } from "react";

class Cards extends Component {
  state = {
    platformClasses: "platform"
  };

  componentDidMount() {
    this.assignPlatformClasses();
  }

  assignPlatformClasses = () => {
    this.setState({
      platformClasses: `{platform} ${this.props.platform}`
    });
  };

  render() {
    return (
      <div key={this.props.item.name} className="game-card">
        <div className="img-container">
          <img
            className="game-img"
            src={this.props.item.background_image}
            alt={this.props.item.name}
          />
        </div>
        <div className="title-container">
          <a className="game-title">{this.props.item.name}</a>
        </div>
        <div className="game-detail-container">
          <p className="game-detail game-rating">
            Rating: {this.props.item.rating}
          </p>
          <p className="game-detail game-release">
            Released: {this.props.item.released}
          </p>
          <a className="game-detail label">Status:</a>
          <a className="game-detail game-status">Owned</a>
        </div>
        <div className="platform pc">
          <p className="platform-name"></p>
        </div>
      </div>
    );
  }
}

export default Cards;
