import React, { Component } from "react";
import Cards from "./Cards";
import AddItem from "./AddItem";
import GameFocus from "./GameFocus";

class MyBacklog extends Component {
  state = {
    addItemVisible: false,
    addItemClasses: "btn link",
    gameFocusClasses: "btn link",
    gameFocusVisible: false
  };

  toggleAddItem = () => {
    if (this.state.addItemVisible) {
      this.setState({
        addItemVisible: false,
        addItemClasses: "btn link"
      });
    } else {
      this.setState({
        addItemVisible: true,
        addItemClasses: "btn btn-selected link",
        gameFocusVisible: false,
        gameFocusClasses: "btn link"
      });
    }
  };

  hideAddItem = () => {
    this.setState({
      addItemVisible: false,
      addItemClasses: "btn link"
    });
  };

  toggleGameFocus = () => {
    if (this.state.gameFocusVisible) {
      this.setState({
        gameFocusVisible: false,
        gameFocusClasses: "btn link"
      });
    } else {
      this.setState({
        addItemVisible: false,
        addItemClasses: "btn link",
        gameFocusVisible: true,
        gameFocusClasses: "btn btn-selected link"
      });
    }
  };

  getFocusGame = async () => {
    const game = await this.props.handleGetGame("the-witcher-3-wild-hunt");

    return game;
  };

  renderContent = () => {
    if (this.state.addItemVisible) {
      return (
        <div className="add-item-container ">
          <AddItem
            handleClose={this.hideAddItem}
            handleSearch={this.props.handleSearch}
            handleAddGame={this.props.handleAddGame}
          />
        </div>
      );
    } else if (this.state.gameFocusVisible /*&& this.props.games.length > 0*/) {
      return (
        <div className="game-focus-container">
          <GameFocus
            game={
              this.getFocusGame()
              /*this.props
                .games[0]*/
            }
            parentComponent={"My Backlog"}
          />
        </div>
      );
    } else {
      return (
        <React.Fragment>
          <div className="card-container">
            <Cards games={this.props.games} parentComponent={"My Backlog"} />
          </div>
        </React.Fragment>
      );
    }
  };

  render() {
    return (
      <div>
        <div className="main">
          <div className="option-bar">
            <div className="option-bar-item">
              <button
                className={this.state.addItemClasses}
                onClick={this.toggleAddItem}
              >
                + Add Item
              </button>
            </div>
            <div className="option-bar-item">
              <button
                className={this.state.gameFocusClasses}
                onClick={this.toggleGameFocus}
              >
                Game Focus
              </button>
            </div>
          </div>
          <div className="content">{this.renderContent()}</div>
        </div>
      </div>
    );
  }
}

export default MyBacklog;
