import React, { Component } from "react";
import NavItem from "./NavItem";

class Navbar extends Component {
  state = {
    aClassName: "nav-link link",
    navItems: this.props.navItems,
    classes: []
  };

  componentDidMount() {
    this.renderButtons();
  }

  renderButtons = () => {
    const navItems = this.state.navItems;
    const navClasses = navItems.map(item => {
      if (item === this.props.pageName) {
        return `${this.state.aClassName} nav-selected`;
      } else {
        return `${this.state.aClassName}`;
      }
    });

    this.setState({ classes: navClasses });
  };

  renderButtons_v2 = () => {
    const navItems = this.state.navItems;
    let navClasses = [];

    for (let i = 0; i < navItems.length; i++) {
      if (i === this.props.pageIndex) {
        navClasses.push(`${this.state.aClassName} nav-selected`);
      } else {
        navClasses.push(`${this.state.aClassName}`);
      }
    }

    console.log("navClasses:", navClasses);

    this.setState({ classes: navClasses });
  };

  drawNabBar = () => {
    const navItemArr = this.state.navItems;

    const navbar = navItemArr.map((item, index) => {
      return (
        <NavItem
          classes={this.state.classes[index]}
          href=""
          key={item}
          value={item}
          handleClick={this.props.handleClick}
        />
      );
    });

    return navbar;
  };

  render() {
    console.log(this.state.classes);
    console.log(this.props.pageIndex);

    return (
      <div className="navbar">
        <nav className="nav">
          <div className="logo">BCKLG</div>
          <ul className="nav-list">{this.drawNabBar()}</ul>
        </nav>
      </div>
    );
  }
}

export default Navbar;
