        return data;
      })
    ).catch(e => {
      console.log(e);
    });
    console.log(results);

    allResults.push(results);

    return <Cards games={allResults} />;
    // Save response to this.cityObjects
  };

  render() {
    return (
      <React.Fragment>
        <h1 className="sub-page-title">Add Item</h1>
        <h3 className="link" onClick={this.props.handleClose}>
          Close
        </h3>
        <div className="search-container">
          <div className="search">
            <input
              type="text"
              className="search-box"
              placeholder="Search Game"
            ></input>
          </div>
        </div>
        {this.renderResults()}
      </React.Fragment>
    );
  }
}

export default AddItem;
